  `right_child` int(11) NOT NULL DEFAULT '0' COMMENT '间接推荐人总数',
  `left_invite` varchar(32) DEFAULT NULL COMMENT '推荐码',
  `right_invite` varchar(32) DEFAULT NULL COMMENT '右推荐码(废弃，不用)',
