  `id_card_type` int(11) DEFAULT NULL COMMENT '实名验证的证件类型，0是默认表示没有认证，1表示中国居民身份证，2是国际护照',
  `id_card` varchar(32) DEFAULT NULL COMMENT '实名验证身份证号码',
  `real_name_status` int(10) NOT NULL DEFAULT '0' COMMENT '实名验证状态，0，未实名验证，1，等待身份证验证，2：身份证号码验证通过',
  `id_card_img1` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '实名验证的图片1',
  `id_card_img2` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '实名验证的图片2',
  `id_card_img3` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '实名验证的图片3',
  `id_card_info` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '当前实名验证的一些额外备注信息，比如验证失败原因',
  `id_card_status` int(3) NOT NULL DEFAULT '0' COMMENT '图片实名验证的状态，0：没有实名验证通过，1： 已经实名验证通过，2：已经提交实名验证，待验证，3：实名验证失败，退回用户重新提交',
  `id_card_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '图片实名认证时间',
  `is_transfer` int(11) DEFAULT '0' COMMENT '特殊账户:1特殊，0普通',
  `brand_permit` int(2) DEFAULT '0' COMMENT '自品牌权限',
  `global_permit` int(2) DEFAULT '0' COMMENT '全平台权限',
  `image` varchar(300) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `idx_mobile` (`mobile`),
  UNIQUE KEY `idx_email` (`email`),
  KEY `idx_invite` (`invite_code`),
  KEY `idx_left_invite` (`left_invite`),
  KEY `idx_right_invite` (`right_invite`)
) ENGINE=InnoDB AUTO_INCREMENT=150 DEFAULT CHARSET=utf8 COMMENT='用户的基本信息';

